INSERT INTO resumes VALUES (128, 20030928, 'строительство, ремонт, архитектура', 'геология, геодезия, картография', 'данных нет', 19781205, 'высшее', 'от 200 у.е.', 'Сидоренко Руслан Сергеевич', '(0562)24-09-41', 'нет', 'ruslan_sid@mail.ru', 9, 7, 7, 8, 10, 'В 2001 г. окончил Харьковский\r\nгосударственный автомобильно-дорожный технический университет. Специальность\r\nинженера-строителя.\r\nС 12.08.2003 работаю в ООО "Мостогидробуд" прорабом. Должностные обязанности:\r\nстроительство гидро-технических сооружений (мостов, путепроводов, набережных,\r\nпортовых причалов).\r\n26.09.2001 г.- 10.12.2002 г. работал мастером по строительству а/д в\r\nстроительном управлении СУ-905 ОАО "Ханты-Мансийскдорстрой"\r\nг. Нефтеюганск, Ханты-Мансийский Автономный Округ, Тюменская обл. (Россия).\r\nЗанимался строительством федеральной автомобильной дороги "Ханты-Мансийск -\r\nНягань". Приобрел опыт: вынесение и\r\nзакрепление оси трассы, разбивочные работы (нивелир, теодолит),\r\nразработка выемок (объемом до 40000 куб.м.), возведение насыпи\r\nзем.полотна, полная выторфовка болот, строительство водонаправляющих дамб,\r\nустройство 2-слойного щебеночного основания, укрепление откосов земляного\r\nполотна, с использованием искусственных материалов (георешетка "Geoveb",\r\nгеотекстиль "Дорнит"), намывка ледовых переправ, строительство искусственных\r\nсооружений (гафриров. метал. трубы диам. 1.5 - 3 м). Работал с техникой: тяжелые\r\nбульдозеры "Kamatsu", экскаваторы "Hitachi", драглайны, грейдеры, катки ДУ-16В,\r\nBOMAG. Отвечал за организацию строительного процесса на месте(имел в подчинении\r\nболее 150 человек, техники более 100 ед.).\r\nДополнительная информация: ПК на уровне пользователя, водительское удостоверение\r\nкатегории В, С.', 1);
